<script setup>
import { onMounted, nextTick } from 'vue';
import { gsap } from 'gsap';
import { Power4, Back, Elastic } from 'gsap/gsap-core';
import { MotionPathPlugin } from 'gsap/MotionPathPlugin';
import { DrawSVGPlugin } from 'gsap/DrawSVGPlugin';
import { ScrambleTextPlugin } from 'gsap/ScrambleTextPlugin';

gsap.registerPlugin(MotionPathPlugin, DrawSVGPlugin);
gsap.registerPlugin(ScrambleTextPlugin);

let blurbs = [
  "Experto en desarrollo web Backend y Frontend.",
  "Soluciones tecnológicas innovadoras y escalables.",
  "Enfoque en aprendizaje continuo y excelencia.",
],
curIndex = 0;

const particlesLoaded = container => {};
  

onMounted(() => {
  nextTick(() => {

    

    
    document.querySelector("#next").addEventListener("click", () => {
  curIndex = (curIndex + 1) % blurbs.length;
  gsap.to(".text-container", {
    scrambleText: {
      text: blurbs[curIndex],
      chars: "upperAndLowerCase",
      revealDelay: 0.2,
      tweenLength: false
    },
    ease: "power2.inOut",
    overwrite: "auto",
    duration: 4.2
  });
});
    // Ensure the DOM is fully rendered before initializing GSAP
    gsap.set(".boop", { drawSVG: "0% 0.75%", autoAlpha: 1, immediateRender: true });
    gsap.set(".boop2", { drawSVG: "99.1% 100%", autoAlpha: 1, immediateRender: true });

    gsap.set(".ball", {
      xPercent: -50,
      yPercent: -50,
      transformOrigin: "50%, 50%",
    });

    function restart() {
      timeline.play(0);
    }

    const startBall = 0.01;
    const endBall = 0.989;

    var timeline = gsap.timeline();
    timeline
      .to(".boop", {
        duration: 1, // Slower animation
        ease: Power4.easeOut,
        drawSVG: "0% 0.1%",
      })
      .to(
        ".boop",
        {
          duration: 0.6, // Slower animation
          ease: Back.easeOut.config(8),
          drawSVG: "0% 0.75%",
        },
        "+=0.2"
      )
      .to(
        ".ball",
        {
          duration: 15, // Slower animation
          ease: "power1.inOut",
          immediateRender: true,
          ease: Elastic.easeOut.config(1, 1.2),
          motionPath: {
            path: document.querySelector(".loop"),
            align: document.querySelector(".loop"),
            start: startBall,
            end: endBall,
          },
        },
        1.3 // Adjusted to start after the boop animation finishes
      )
      .to(
        ".boop2",
        {
          duration: 1, // Slower animation
          ease: Power4.easeOut,
          drawSVG: "99.9% 100%",
        },
        6.4
      )
      .to(
        ".boop2",
        {
          duration: 0.6, // Slower animation
          ease: Back.easeOut.config(8),
          drawSVG: "99.1% 100%",
          delay: 0.16,
        },
        7.1
      )
      .to(
        ".ball",
        {
          duration: 15, // Slower animation
          ease: Elastic.easeOut.config(1, 1.2),
          motionPath: {
            path: document.querySelector(".loop"),
            align: document.querySelector(".loop"),
            start: endBall,
            end: startBall,
          },
        },
        7.3
      )
      .add(restart, 12.2);
  });
});
</script>


